function cargarAccionesArbolProblemas(){$("#arbolProblemas").on("change",function(){var e=$("#arbolProblemas"),t={};t.problema=e.find("#p1").val(),t.causas={},t.causas.uno=e.find("#c1").val(),t.causas.dos=e.find("#c2").val(),t.causas.tres=e.find("#c3").val(),t.efectos={},t.efectos.uno=e.find("#e1").val(),t.efectos.dos=e.find("#e2").val(),t.efectos.tres=e.find("#e3").val(),$(".jsonArbolProblemas").val(JSON.stringify(t))})}function cargarArbolProblemas(){if($(".jsonArbolProblemas").val()!=""){var e=JSON.parse($(".jsonArbolProblemas").val()),t=$("#arbolProblemas");t.find("#p1").val(e.problema.toString()),t.find("#c1").val(e.causas.uno.toString()),t.find("#c2").val(e.causas.dos.toString()),t.find("#c3").val(e.causas.tres.toString()),t.find("#e1").val(e.efectos.uno.toString()),t.find("#e2").val(e.efectos.dos.toString()),t.find("#e3").val(e.efectos.tres.toString())}};