function cargarAccionesTablaRecursos(){$("#btn_agregarRecurso").on("click",function(){var e={};e.tipoRecurso=$("#sel_tipoMat_agregarRecurso").val(),e.descripcion=$("#txt_desc_agregarRecurso").val(),e.cantidad=$("#txt_cant_agregarRecurso").val(),e.costo=$("#txt_cost_agregarRecurso").val(),addElementToResourcesTable(e)}),$("#tablaResourcesDef").on("change",function(){guardarJsonTablaRecursos()})}function addElementToResourcesTable(e){var t="<tr class='renglonTablaRecursos'>";t+="<td class='tipoRecurso'>"+e.tipoRecurso+"</td>",t+="<td class='descripcion'>"+e.descripcion+"</td>",t+="<td class='cantidad'>"+e.cantidad+"</td>",t+="<td class='costo'>"+e.costo+"</td>",t+="<td class='txtResourcesSubTotal'>"+e.costo*e.cantidad+"</td>",t+="</tr>",$("#txtResourcesTotal").remove(),$("#tablaResourcesDef").append(t),agregarColumnaTotal(),guardarJsonTablaRecursos()}function isInputCorrect(){var e=$("#sel_tipoMat_agregarRecurso").val();return e=="0"?(alert("Selecciona el tipo de Recurso (humano, material)"),!1):!0}function guardarJsonTablaRecursos(){var e=$("#tablaResourcesDef").find(".renglonTablaRecursos");e.find(".tipoRecurso");var t=new Array,n={};$(e).each(function(e,r){console.log($(r).find(".tipoRecurso").html()),console.log($(r).find(".descripcion").html()),console.log($(r).find(".cantidad").html()),console.log($(r).find(".costo").html()),console.log($(r).find(".txtResourcesSubTotal").html()),console.log("-----------"),n.tipoRecurso=$(r).find(".tipoRecurso").html(),n.descripcion=$(r).find(".descripcion").html(),n.cantidad=$(r).find(".cantidad").html(),n.costo=$(r).find(".costo").html(),n.resourcesSubTotal=$(r).find(".txtResourcesSubTotal").html(),t.push(n),$(".jsonTablaRecursos").val(JSON.stringify(t))})}function agregarColumnaTotal(){var e=$(".txtResourcesSubTotal"),t=0;e.each(function(e,n){t+=parseInt($(n).html())});var n="<tr id='txtResourcesTotal'><td colspan='4' style='border: inset 0pt; text-align: right; font-size: 20px; font-weight: bold; padding-right: 12px;'>Total:</td><td>"+t+"</td></tr>";$("#tablaResourcesDef").append(n)};