function cargarAccionesTablaRecursos(){$("#btn_agregarRecurso").on("click",function(){var e={};e.tipoRecurso=$("#sel_tipoMat_agregarRecurso").val(),e.descripcion=$("#txt_desc_agregarRecurso").val(),e.cantidad=$("#txt_cant_agregarRecurso").val(),e.costo=$("#txt_cost_agregarRecurso").val(),addElementToResourcesTable(e)})}function addElementToResourcesTable(e){var t="<tr>";t+="<td>"+e.tipoRecurso+"</td>",t+="<td>"+e.descripcion+"</td>",t+="<td>"+e.cantidad+"</td>",t+="<td>"+e.costo+"</td>",t+="<td class='txtResourcesSubTotal'>"+e.costo*e.cantidad+"</td>",t+="</tr>",$("#txtResourcesTotal").remove(),$("#tablaResourcesDef").append(t);var n=$(".txtResourcesSubTotal"),r=0;n.each(function(e,t){r+=parseInt($(t).html())});var i="<tr id='txtResourcesTotal'><td colspan='4' style='border: inset 0pt; text-align: right; font-size: 20px; font-weight: bold; padding-right: 12px;'>Total:</td><td>"+r+"</td></tr>";$("#tablaResourcesDef").append(i)}function isInputCorrect(){var e=$("#sel_tipoMat_agregarRecurso").val();return e=="0"?(alert("Selecciona el tipo de Recurso (humano, material)"),!1):!0};