function cargarArbolProblemas(){var e=JSON.parse($(".jsonArbolProblemas").val()),t=$("#arbolProblemas");t.find("#p1").val(e.problema.toString()),t.find("#c1").val(e.causas.uno.toString()),t.find("#c2").val(e.causas.dos.toString()),t.find("#c3").val(e.causas.tres.toString()),t.find("#e1").val(e.efectos.uno.toString()),t.find("#e2").val(e.efectos.dos.toString()),t.find("#e3").val(e.efectos.tres.toString())}function cargarDiagramaActividades(){var e=$("#diagramaActividades"),t=$("#arbolProblemas");e.find("#cAct1").val(t.find("#c1").val()),e.find("#cAct2").val(t.find("#c2").val()),e.find("#cAct3").val(t.find("#c3").val())};